import React, { useState } from "react";

import "./NotePage.css";
import Sidebar from "../SavedNotesBar/Components/SavedNotesBar/Sidebar.js";

const NotePage = (props) => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const handleSubmit = (e) => {
    /* to avoid browser refresh on form submit */
    e.preventDefault();
    /* we will add functionality later to add notes to the note context */
    // eslint-disable-next-line no-restricted-globals
    history.push("/notes");
  };
  return (
    <div className="app">
      <div>
        <Sidebar />
      </div>
      <div>
        <form onclick={handleSubmit}>
          <legend>Create Notes</legend>

          <input
            className="notes__controls__title"
            type="text"
            placeholder="Enter Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <textarea
            className="notes__controls__textarea"
            placeholder="type your note..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            required
          />

          <div>
            <input className="notes__actions" type="submit" value="Save Note" />
          </div>
        </form>
      </div>
    </div>
  );
};
export default NotePage;
