import React, { useEffect } from "react";

import { NoteContexts } from "../NoteContext/NoteContext";
import NoteItem from "../NotesApp/NoteItem";

import "./Sidebar.css";

const Sidebar = () => {

  const { data } = NoteContexts();

  useEffect(() => {
    console.log(data);
  }, [data]);

  return (
    <div className="side_navbar">
      <div className="header text-center">My Notes</div>
      <div className="side_navbar d-flex align-items-center">
        {data.map((note, index) => {
            return <NoteItem key={index} note={note}/>
        })}
      </div>
    </div>
  );
};

export default Sidebar;
