import { createContext, useContext, useState } from "react";

const NoteContext = createContext({});

export function NoteContexts() {
  const contextValue = useContext(NoteContext);

  return contextValue;
}

export default function NoteProvider(props) {
  const [data, setData] = useState([]);

  function setNote(title, description) {
    console.log(data);
    setData([
      ...data,
      { id: Math.round(Math.random() * 10000), title, description },
    ]);
  }

  function deleteNote(id) {
    setData(data.filter((note) => note.id !== id));
  }

  function updateNote(newNote) {
    const newData = data.map((note) => {
      if (note.id === newNote.id) {
        note.title = newNote.title;
        note.description = newNote.description;
      }
      return note;
    });
    setData([...newData])
  }

  const value = { data, setNote, deleteNote, updateNote };
  return <NoteContext.Provider value={value} {...props} />;
}
