import React, { useState } from "react";

import "./NotePage.css";

const NotePage = (props) => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const handleSubmit = (e) => {
    /* to avoid browser refresh on form submit */
    e.preventDefault();
    /* we will add functionality later to add notes to the note context */
    // eslint-disable-next-line no-restricted-globals
    history.push("/notes");
  };
  return (
    <div className="app">

        <form onclick={handleSubmit}>  
        <legend>Create Notes</legend>

          <input
            className="notes__controls"
            type="text"
            placeholder="Enter Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

        <div>
          <textarea
            className="notes__controls"
            placeholder="type your note..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            required
          />
        </div>
        <div>
          <input className="notes__actions" type="submit" value="Save Note" />
        </div>
        </form>
    </div>
  );
};
export default NotePage;
