import React, { useState } from "react";

import NoteItem from "../NotesApp/NoteItem";
import NotePage from "../NotesApp/NotePage";
import "./Sidebar.css";

const Sidebar = () => {
  const note1 = { id: 1, title: "test 1", text: "hello world note 1" };
  const note2 = { id: 2, title: "other note", text: "hello world note 2" };

  const [taskList, setTaskList] = useState([])

  const saveTask = (taskObj) =>{
    let tempList = taskList
    tempList.push(taskObj)
    setTaskList(tempList)
  }

  return (
    <div className="side_navbar">
      <div className="header text-center">My Notes</div>
      <div className="side_navbar d-flex align-items-center">
      <NoteItem note={note1} />
      <NoteItem note={note2} />
      </div>
    </div>
  );
};

export default Sidebar;
