import "./NoteItem.css";

import NoteProvider from "../NoteContext/NoteContextProvider";

const NoteItem = (props) => {
  const { note } = props;
  // eslint-disable-next-line no-unused-vars
  const handleClick = () =>
  {
    NoteProvider.updateNote(note);
  }
  const { id, title, text } = note;
  return (
    <div className="note-item">
      <div className="note-title">
        {id}. {title}
      </div>
      <div className="note-btn-container">
        <button className="note-btn edit-btn" onClick={handleClick}>Edit</button>
        <button className="note-btn delete-btn">Delete</button>
      </div>
    </div>
  );
};
NoteItem.defaultProps = { note: { id: "", title: "", text: "" } };
export default NoteItem;
