import React, { useState } from "react";

import "./NotePage.css";
import Sidebar from "../SavedNotesBar/Sidebar";

const NotePage = (props) => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");

  const handleChange = (e) => {
    const{name, value} = e.target

    if(name === "taskName"){
      setTitle(value)
    }else{
      setText(value)
    }
  }

  const handleSubmit = (e) => {
    /* to avoid browser refresh on form submit */
    e.preventDefault();
    /* we will add functionality later to add notes to the note context */
    // eslint-disable-next-line no-restricted-globals
    history.push("/notes");
  };
  return (
    <div className="flex-container app">
      <div className="app-1">
        <div>
          <Sidebar />
        </div>
      </div>
      <div className="app-2">
        <div>
          <form onclick={handleSubmit}>
          <div className="header text-center">Create Notes</div>
            <input
              className="notes__controls__title"
              name="taskName"
              type="text"
              placeholder="Enter Title"
              value={title}
              onChange={handleChange}
              required
            />
            <textarea
              className="notes__controls__textarea"
              name="description"
              placeholder="type your note..."
              value={text}
              onChange={(e) => setText(e.target.value)}
              required
            />
            <div>
              <input
                className="notes__actions mt-2"
                onClick={handleSubmit}
                type="submit"
                value="Save Note"
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
export default NotePage;
