import React, { useEffect, useState } from "react";

import "./NotePage.css";
import Sidebar from "../SavedNotesBar/Sidebar";
import { NoteContexts } from "../NoteContext/NoteContext";

const NotePage = (props) => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");

  const { data, setNote, update, updateNote, raiseUpdate, setLocalData } =
    NoteContexts();

  const handleSubmit = (e) => {
    /* to avoid browser refresh on form submit */
    e.preventDefault();

    if (update.update) {
      updateNote({ id: update.id, title: title, description: text });
      raiseUpdate(false, {});
      reset();
    } else {
      setNote(title, text);
      reset();
    }
  };

  function reset() {
    setTitle("");
    setText("");
  }

  useEffect(() => {
    if (localStorage.getItem("data") !== null) {
      setLocalData(JSON.parse(localStorage.getItem("data")));
    }
  }, []);

  window.onbeforeunload = () => {
    localStorage.setItem("data", JSON.stringify(data));
  };

  useEffect(() => {
    if (update.update) {
      setTitle(update.title);
      setText(update.description);
    }
    console.log(data);
  }, [data, update]);

  return (
    <div className="flexcontainer app">
      <div className="app-1">
        <div>
          <Sidebar />
        </div>
      </div>
      <div className="app-2">
        <form onSubmit={handleSubmit}>
          <div>
            <div className="header text-center">Create Notes</div>
            <input
              className="notes__controls__title"
              type="text"
              placeholder="Enter Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
            <textarea
              className="notes__controls__textarea"
              placeholder="type your note..."
              value={text}
              onChange={(e) => setText(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              className="notes__actions mt-2"
              type="submit"
              value={update.update ? "Update Note" : "Save Note"}
            />
          </div>
        </form>
      </div>
    </div>
  );
};
export default NotePage;
