import React, { useEffect, useState } from "react";

import "./NotePage.css";
import Sidebar from "../SavedNotesBar/Sidebar";
import { NoteContexts } from "../NoteContext/NoteContext";

const NotePage = (props) => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");

const {data,setNote}  = NoteContexts();

  const handleSubmit = (e) => {
    /* to avoid browser refresh on form submit */
    e.preventDefault();
    /* we will add functionality later to add notes to the note context */
    // eslint-disable-next-line no-restricted-globals
    history.push("/notes");

    setNote(title, text);
    console.log()


  };

  useEffect(()=> {
    console.log(data);
  },[])

  return (
    <div className="flex-container app">
      <div className="app-1">
        <div>
          <Sidebar />
        </div>
      </div>
      <div className="app-2">
        <div>
          <form onSubmit={handleSubmit}>
            <div className="header text-center">Create Notes</div>
            <input
              className="notes__controls__title"
              type="text"
              placeholder="Enter Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
            <textarea
              className="notes__controls__textarea"
              placeholder="type your note..."
              value={text}
              onChange={(e) => setText(e.target.value)}
              required
            />
            <div>
              <input
                className="notes__actions mt-2"
                type="submit"
                value="Save Note"
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
export default NotePage;
